// HMAC Signature Validation for GitHub Webhooks
const crypto = require('crypto');

const items = $input.all();
const firstItem = items[0];

// Get headers and body from webhook
const headers = firstItem.json.headers || {};
const body = firstItem.json.body;

// Check if this is a localhost request (bypass signature validation)
const host = headers['host'] || '';
const isLocalhost = host.includes('localhost') || host.includes('127.0.0.1');

if (isLocalhost) {
  console.log('Localhost request - skipping signature validation');
  return items;
}

// Validate GitHub signature for non-localhost requests
const secret = $env.N8N_WEBHOOK_SECRET;
const signature = headers['x-hub-signature-256'];

if (!signature) {
  throw new Error('Missing X-Hub-Signature-256 header');
}

if (!secret) {
  throw new Error('N8N_WEBHOOK_SECRET environment variable not set');
}

// Calculate HMAC signature
const hmac = crypto
  .createHmac('sha256', secret)
  .update(JSON.stringify(body))
  .digest('hex');

const expectedSignature = `sha256=${hmac}`;

// Verify signature matches
if (signature !== expectedSignature) {
  throw new Error('Invalid GitHub signature');
}

console.log('Signature validated successfully');
return items;

// Extract Event Data from GitHub Webhook
const items = $input.all();
const firstItem = items[0];

const payload = firstItem.json.body || {};
const headers = firstItem.json.headers || {};
const eventType = headers['x-github-event'] || 'unknown';

// Build event data object
const eventData = {
  timestamp: new Date().toISOString(),
  event_type: eventType,
  delivery_id: headers['x-github-delivery'] || 'unknown',
  repository: payload.repository?.full_name || 'unknown',
  sender: payload.sender?.login || 'unknown',
  raw_payload: payload
};

// Extract event-specific data
if (eventType === 'push') {
  eventData.branch = payload.ref?.replace('refs/heads/', '') || 'unknown';
  eventData.commits = payload.commits?.length || 0;
  eventData.head_commit = payload.head_commit || {};
  eventData.compare_url = payload.compare || '';
} else if (eventType === 'pull_request') {
  eventData.action = payload.action || 'unknown';
  eventData.pr_number = payload.pull_request?.number || 0;
  eventData.pr_title = payload.pull_request?.title || '';
  eventData.pr_url = payload.pull_request?.html_url || '';
  eventData.pr_state = payload.pull_request?.state || '';
  eventData.pr_merged = payload.pull_request?.merged || false;
}

return [{ json: eventData }];

// Log Payload to File
const fs = require('fs');
const path = require('path');

const items = $input.all();
const data = items[0].json;

// Create log directory if it doesn't exist
const logDir = '.tmp/n8n-endpoint/github-discord';
const timestamp = new Date().toISOString().replace(/:/g, '-').split('.')[0];
const filename = `${timestamp}.json`;
const filepath = path.join(logDir, filename);

try {
  // Create directory recursively
  if (!fs.existsSync(logDir)) {
    fs.mkdirSync(logDir, { recursive: true });
  }

  // Write log file
  fs.writeFileSync(filepath, JSON.stringify(data, null, 2));
  console.log(`Logged payload to ${filepath}`);

  return [{
    json: {
      ...data,
      log_file: filepath
    }
  }];
} catch (error) {
  // Log error but don't fail workflow (logging is non-critical)
  console.error('Failed to write log file:', error.message);
  return items;
}

// Format Discord Message
const items = $input.all();
const data = items[0].json;
const eventType = data.event_type;
const payload = data.raw_payload || {};

let discordMessage = '';

if (eventType === 'push') {
  const branch = data.branch || 'unknown';
  const commits = payload.commits || [];
  const sender = payload.sender || {};
  const repo = payload.repository || {};
  const compareUrl = data.compare_url || '';

  discordMessage = `ðŸš€ **New Push to \`${repo.full_name || 'unknown'}\`**\n\n`;
  discordMessage += `**Branch**: \`${branch}\`\n`;
  discordMessage += `**Pusher**: [${sender.login || 'unknown'}](${sender.html_url || '#'})\n\n`;

  if (commits.length > 0) {
    discordMessage += `**Commits** (${commits.length}):\n`;
    const displayCommits = commits.slice(0, 5);

    displayCommits.forEach(commit => {
      const shortSha = (commit.id || 'unknown').substring(0, 7);
      const message = (commit.message || 'No message').split('\n')[0];
      discordMessage += `â€¢ [\`${shortSha}\`](${commit.url || '#'}) - ${message}\n`;
    });

    if (commits.length > 5) {
      discordMessage += `â€¢ ... and ${commits.length - 5} more commits\n`;
    }
  }

  if (compareUrl) {
    discordMessage += `\n[View Diff](${compareUrl})`;
  }

} else if (eventType === 'pull_request') {
  const pr = payload.pull_request || {};
  const action = data.action || 'unknown';
  const repo = payload.repository || {};

  let emoji = 'ðŸ“¬';
  let actionText = 'Pull Request';

  if (action === 'closed' && pr.merged) {
    emoji = 'âœ…';
    actionText = 'Pull Request Merged';
  } else if (action === 'closed') {
    emoji = 'âŒ';
    actionText = 'Pull Request Closed';
  } else if (action === 'opened') {
    emoji = 'ðŸ“¬';
    actionText = 'Pull Request Opened';
  } else {
    emoji = 'ðŸ“¬';
    actionText = `Pull Request ${action}`;
  }

  discordMessage = `${emoji} **${actionText} in \`${repo.full_name || 'unknown'}\`**\n\n`;
  discordMessage += `**Title**: ${pr.title || 'No title'}\n`;
  discordMessage += `**Author**: [${pr.user?.login || 'unknown'}](${pr.user?.html_url || '#'})\n`;
  discordMessage += `**Branch**: \`${pr.head?.ref || 'unknown'}\` â†’ \`${pr.base?.ref || 'unknown'}\`\n\n`;

  if (pr.body && pr.body.length > 0) {
    const description = pr.body.substring(0, 200);
    const ellipsis = pr.body.length > 200 ? '...' : '';
    discordMessage += `**Description**:\n${description}${ellipsis}\n\n`;
  }

  discordMessage += `[View Pull Request](${pr.html_url || '#'})`;

} else {
  // Unknown event type
  discordMessage = `ðŸ“« **GitHub Event: ${eventType}**\n\n`;
  discordMessage += `**Repository**: ${data.repository || 'unknown'}\n`;
  discordMessage += `**Sender**: ${data.sender || 'unknown'}\n`;
}

return [{
  json: {
    content: discordMessage,
    event_data: data
  }
}];

// Format Success Response
return [{
  json: {
    status: 'success',
    message: 'Notification sent to Discord',
    timestamp: new Date().toISOString()
  }
}];
